import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import editIcon from '../assets/edit.png'
import deleteIcon from '../assets/delete.png'
import { allUserContext } from '../userContext'

export const Admin = () => {


    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
    
        axios.get('http://localhost:5000/admin', {withCredentials: true})
        .then(res => {

            console.log(res)

            const users = [];
            const data = res.data
            for (const key in data) {
                users.push({
                    id: key,
                    name: data[key].name, 
                    email: data[key].email, 
                    date: data[key].date,
                    isAdmin: data[key].isAdmin
                })
            }

            setUser(users)
        })
      }, [])

    

      const deleteUserHandler = id => {

        axios.delete(`http://localhost:5000/deleteUser/${id}`)
        .then(res => {
            const index = user.findIndex(list => list.id === id);
            const userArr = [...user]

            userArr.splice(index, 1)
            setUser(userArr)
            console.log(user)
        }) 
      }

      const editUser = id => {
        navigate('/admin/edituser', {state: {id: id}})
        
      }

      const tableData = () => {

        return user ? user.map(users => {
            return (
                <tr key={users.id}>
                    <td className='p-4 bg-gray-100'>{users.name}</td>
                    <td className='p-4 bg-gray-100'>{users.email}</td>
                    <td className='p-4 bg-gray-100'>{users.isAdmin ? 'Admin' : 'User'}</td>
                    <td className='p-4 bg-gray-100'>
                        <img src={editIcon} onClick={editUser.bind(this, users.id)} alt="delete" className='w-5 cursor-pointer' />
                    </td>
                    <td className='p-4 bg-gray-100'>
                        <img src={deleteIcon} onClick={deleteUserHandler.bind(this, users.id)} alt="delete" className='w-5 cursor-pointer' />
                    </td>
                </tr>
            )
        }) : null
      }

    return (
        <allUserContext.Provider value={{user, setUser}}>
            <div className='w-full h-full flex flex-col justify-center items-center text-left'>
                <h1 className='text-4xl font-semibold'>Users</h1>

                <table className='w-2/4 m-auto bg-gray-200 text-center rounded-md mt-10'>
                    <thead>
                        <tr className='w-40'>
                            <th className=' py-2'>NAME</th>
                            <th>EMAIL</th>
                            <th>ROLES</th>
                        </tr>
                    </thead>

                    <tbody>
                        {tableData()}
                    </tbody>

                </table>
            </div>
        </allUserContext.Provider>
        
    )
}
