import React, { useContext, useEffect, useState } from 'react'
import axios from 'axios'
import { userContext } from '../userContext'
import { Lists } from '../Components/UI/Lists/Lists'
import { Control } from '../Components/UI/Control/Control'

export const Home = () => {

    const [dark, setDarkTheme] = useState(false);
    const [data, setData] = useState(null);
    const [filter, setFilter] = useState('All');
    const [input, setInput] = useState('')
  
  
    useEffect(() => {
      axios.get('https://react-hooks-1b64f-default-rtdb.firebaseio.com/todo.json')
      .then(res => {
        const todos = [];
        const data = res.data
        for (const key in data) {
            todos.push({id: key, data: data[key].name, check: data[key].check})
          }
        setData(todos)
  
      })
    }, [])
  
    useEffect(() => {
        const rootEl = document.getElementById('root')
        return dark ? rootEl.classList.add('dark') : rootEl.classList.remove('dark');
  
    }, [dark])

    const onSubmitHandler = e => {
        e.preventDefault()
        // axios.post('https://react-hooks-1b64f-default-rtdb.firebaseio.com/todo.json',
        //  {
        //      'name': input,
        //       check: false
        //     }
        // )
        // .then(res => {
        //     const todoItem = {id: res.data.name, data: input, check:false}
        //     props.setData(props.data.concat(todoItem))

        //     console.log(props.data)
            
        // })
        // .catch(err => console.log(err))
    }
  
  
    return (
      
      <div className="w-full h-screen bg-gray-100">
     
        <section className="tablet:m-auto  bg-no-repeat bg-cover h-52 w-full bg-desktop-light dark:bg-desktop-dark py-10 px-10">
          <header className=" max-w-lg m-auto">
            <div className="bg-white mt-5 rounded-input ">
                <form onSubmit={onSubmitHandler}>
                    <input placeholder="Create a new todo..."  className="text-sm py-3 px-5 w-full rounded-xl focus:border-white focus:outline-none" value={input} onChange={(e) => setInput(e.target.value)}/>
                </form>
            </div>
          </header>
  
          <main className="max-w-lg m-auto ">
            <Lists data={data} setData={setData} filter={filter} />
          </main>
  
          <footer className="max-w-lg m-auto">
            <Control setFilter={setFilter} filter={filter}/>
          </footer>
        </section>
    
      </div>
    )
}
