import React, { useContext } from 'react'
import { useNavigate } from 'react-router-dom'
import { Button } from '../UI/Button/Button';
import { NavLink } from 'react-router-dom';
import { useCookies  } from 'react-cookie'
import { userContext } from '../../userContext';

export const Nav = () => {

    const [cookies, setCookie, removeCookie] = useCookies(["user"]);
    const navigate = useNavigate();
    const user = useContext(userContext)
    const redirectLogin = () => navigate('/login')

    const logoutHandler = () => {
        removeCookie('token')
        navigate('/')
        user.setData(null)
    }

    return (
        <div className="flex w-full justify-between py-5 px-10 font-mono bg-gray-100">
            <section className="flex w-1/5 justify-around font-bold">
                <p className=" cursor-pointer"><NavLink to="/">HOME</NavLink></p>
                {user.data ? <p className="cursor-pointer"><NavLink to="/profile">PROFILE</NavLink></p> : null}
                {/* {user.data.isAdmin ? <p className="cursor-pointer"><NavLink to="/admin">Manage</NavLink></p> : null} */}
            </section>  

            <section className="flex justify-between">
                <Button onClick={user.data ? logoutHandler : redirectLogin} name={user.data ? "LOGOUT" : "LOGIN"}/>
            </section>
        </div>
    )
}
