import React from 'react'

export const Modal = props => {
    return (
        <div className='bg-white rounded-lg max-w-lg' style={{
            transform: this.props.show ? 'translateY(0)' : 'translateY(-100vh)',
            opacity: this.props.show ? '1' : '0'
        }}>
            {props.children}
        </div>
    )
}
