import React from 'react'
import deleteIcon from '../../../assets/delete.png'

export const Modal = props => {
    return (
        <div className='bg-white rounded-lg max-w-lg w-1/3 h-1/3 fixed left-1/3 top-1/3 transition-all z-50 flex flex-col justify-center items-center text-left' 
        
        style={{
            transform: props.show ? 'translateY(0)' : 'translateY(-100vh)',
            opacity: props.show ? '1' : '0'
        }}>
            {props.children}

            <img src={deleteIcon}  alt="delete" className='w-5 cursor-pointer' />
        </div>
    )
}
